CREATE DATABASE TheaterDB

use TheaterDB

CREATE TABLE Shows (
    ShowId INT NOT NULL IDENTITY,
    Title NVARCHAR(MAX) NULL, 
    Author NVARCHAR(MAX) NULL,
    Director NVARCHAR(MAX) NULL,
    Genre NVARCHAR(MAX) NULL,
    Age INT NOT NULL,
    Date DATETIME2 NOT NULL,
    Length NVARCHAR(MAX) NULL,
    Price decimal(18,2) NOT NULL,
    Poster NVARCHAR(MAX) NULL,
    Banner NVARCHAR(MAX) NULL,
    Scene NVARCHAR(MAX) NULL,
    Overview NVARCHAR(MAX) NULL,
    CONSTRAINT [PK_Shows] PRIMARY KEY ([ShowId])
);

CREATE TABLE Users (
    UserId INT IDENTITY,
    UserName NVARCHAR(MAX),
    UserLastname NVARCHAR(MAX),
    Email NVARCHAR(MAX),
    Password NVARCHAR(MAX),
    PhoneNumber NVARCHAR(MAX),
    IsAdmin BIT,
    CONSTRAINT [PK_Users] PRIMARY KEY ([UserId])
);

CREATE TABLE Sessions (
    SessionId INT NOT NULL IDENTITY,
    Hour NVARCHAR(MAX) NULL,
    TotalSeats INT NOT NULL,
    Notes NVARCHAR(MAX) NULL,
    ShowId INT NOT NULL,
    Title NVARCHAR(MAX) NULL,
    Poster NVARCHAR(MAX) NULL,
    Date DATETIME2 NOT NULL,
    CONSTRAINT [PK_Sessions] PRIMARY KEY ([SessionId])
);


    
